$(function(){var t=$(".isotope > .row").imagesLoaded(function(){t.isotope({itemSelector:".item",layoutMode:"masonry",getSortData:{interest:"[data-interest]",alpha:"h3"}}),$(".filters").on("click","button",function(){var o=$(this).attr("data-filter");t.isotope({filter:o})}),$(".sorts").on("click","button",function(){var o=$(this).attr("data-sort");o=o.split(","),t.isotope({sortBy:o})}),$(".filters, .sorts").each(function(t,o){var a=$(o);a.on("click","button",function(){a.find(".active").removeClass("active"),$(this).addClass("active")})})});$(".item").each(function(){var t="rgb("+(Math.floor(57*Math.random())+200)+","+(Math.floor(57*Math.random())+200)+","+(Math.floor(57*Math.random())+200)+")";$(this).css("background-color",t)})});