$(function(){var t=$(".isotope > .row");t.isotope({itemSelector:".item",layoutMode:"masonry",getSortData:{interest:"[data-interest]",alpha:"h3"}}),$(".filters").on("click","button",function(){var o=$(this).attr("data-filter");t.isotope({filter:o})}),$(".sorts").on("click","button",function(){var o=$(this).attr("data-sort");o=o.split(","),t.isotope({sortBy:o})}),$(".filters, .sorts").each(function(t,o){var i=$(o);i.on("click","button",function(){i.find(".active").removeClass("active"),$(this).addClass("active")})})});